input = '''



'''
input_l = input.split("\n")

location = { 'x': 1, 'y': 1 }

def get_key(x, y):
    return str(3 * y + x + 1)

code = ""

for s in input_l:
    for c in s:
        if c is 'U':
            location['y'] -= 1 if location['y'] is not 0 else 0
        elif c is 'D':
            location['y'] += 1 if location['y'] is not 2 else 0
        elif c is 'L':
            location['x'] -= 1 if location['x'] is not 0 else 0
        else:
            location['x'] += 1 if location['x'] is not 2 else 0
    code += get_key(location['x'], location['y'])

print("Code: {}".format(code))
